import React from 'react';
import "@fortawesome/fontawesome-free/css/all.min.css";
import "bootstrap-css-only/css/bootstrap.min.css";
import "mdbreact/dist/css/mdb.css";
import API from '../../service/signup.service';
import { MDBContainer, MDBBtn, MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter } from 'mdbreact';

class ResetPassword extends React.Component {

    /** First Constructor Call */
    constructor(props) {
        super(props);
        this.state = {
            password: ''
        }
        this.ResetPassword = this.ResetPassword.bind(this);
        this.handleChangeEvent = this.handleChangeEvent.bind(this);
    }

    componentDidMount() {
        console.log("query=",this.props.location.pathname.split('/')[2]);
    }

    /** onChange event  */
    handleChangeEvent(event) {
        event.preventDefault();
        const state = this.state
        state[event.target.name] = event.target.value;
        this.setState(state);
    }

    ResetPassword() {
        // const obj = {
        //     email: this.state.email
        // }
        // API.ForgotPassword(obj).
        //     then((findresponse) => {
        //         console.log("response==", findresponse);
        //     }).catch({ status: 500, message: 'Internal Server Error' });
    }

    render() {
        return (
            <div>
                <MDBContainer>
                    <MDBModalBody>
                        <form>
                            <label
                                className="grey-text"
                            >
                                Your password
                                </label>
                            <input
                                type="password"
                                name="password"
                                id="defaultFormRegisterPasswordEx"
                                className="form-control"
                                value={this.state.password}
                                onChange={this.handleChangeEvent}
                            />
                        </form>
                    </MDBModalBody>
                    <MDBModalFooter>
                        <MDBBtn color="primary" onClick={this.ResetPassword}>Send</MDBBtn>
                    </MDBModalFooter>
                </MDBContainer>
            </div>
        );
    }
}

export default ResetPassword;
