import axios from 'axios';
import { config } from '../config';

export default {
    /** User Signup */
    Signup: (obj) => {
        return axios.post(config.baseApiUrl + "api/signup",obj)
        .then(response => {
            return response;
        }).catch({ status: 500, message: 'Internal Server Error' });
    },
    /** User Login */
    Login: (obj) => {
        console.log("obj==",obj);
        return axios.post(config.baseApiUrl + "api/login",obj)
        .then(response => {
            return response;
        }).catch({ status: 500, message: 'Internal Server Error' });
    },
    /** Forgot Password */ 
    ForgotPassword: (obj) => {
        return axios.post(config.baseApiUrl + "api/resetPassword",obj)
        .then(response => {
            return response;
        }).catch({ status: 500, message: 'Internal Server Error' });
    },

     /**
     *  @param {string} token
     *  Authenticate with token
     */
    authenticate: () => {
        const Token = localStorage.getItem('token');
        const headers = {
            'Content-Type': 'application/json',
            'Authorization': 'JWT fefege...',
            'token': Token
        }
        return axios.get('http://localhost:4000/user/checkToken', { headers: headers }).then((response) => {
            return response;
        }).catch({ status: 500, message: 'Internal Server Error' });
    }
 }
