import React from 'react';
import "@fortawesome/fontawesome-free/css/all.min.css";
import "bootstrap-css-only/css/bootstrap.min.css";
import "mdbreact/dist/css/mdb.css";
import API from '../../service/signup.service';
import { MDBContainer, MDBBtn, MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter } from 'mdbreact';

class ForgotPassword extends React.Component {

    /** First Constructor Call */
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            modal: false
        }
        this.forgotpassword = this.forgotpassword.bind(this);
        this.handleChangeEvent = this.handleChangeEvent.bind(this);
    }

    componentDidMount() {
        console.log("query=", this.props.location);
        this.toggle();
    }

    toggle = () => {
        this.setState({
            modal: !this.state.modal
        });
    }

    /** onChange event  */
    handleChangeEvent(event) {
        event.preventDefault();
        const state = this.state
        state[event.target.name] = event.target.value;
        this.setState(state);
    }

    forgotpassword() {
        const obj = {
            email: this.state.email
        }
        API.ForgotPassword(obj).
            then((findresponse) => {
                console.log("response==", findresponse);
            }).catch({ status: 500, message: 'Internal Server Error' });
    }

    render() {
        return (
            <div>
                <MDBContainer>
                    <MDBModal isOpen={this.state.modal} toggle={this.toggle}>
                        <MDBModalHeader toggle={this.toggle}>ForgotPassword</MDBModalHeader>
                        <MDBModalBody>
                            <form>
                                <label className="grey-text">
                                    Enter Your email
                          </label>
                                <input
                                    type="email"
                                    name="email"
                                    className="form-control"
                                    value={this.state.email}
                                    onChange={this.handleChangeEvent}
                                />
                                <br />
                            </form>
                        </MDBModalBody>
                        <MDBModalFooter>
                            <MDBBtn color="primary" onClick={this.forgotpassword}>Send</MDBBtn>
                        </MDBModalFooter>
                    </MDBModal>
                </MDBContainer>
            </div>
        );
    }
}

export default ForgotPassword;
