import React from 'react';
import { MDBContainer, MDBRow, MDBCol, MDBBtn } from 'mdbreact';
import "@fortawesome/fontawesome-free/css/all.min.css";
import "bootstrap-css-only/css/bootstrap.min.css";
import "mdbreact/dist/css/mdb.css";
import API from '../../service/signup.service';
import { Link } from 'react-router-dom';
import './login.css';

class Login extends React.Component {

  /** First Constructor Call */
  constructor(props) {
    super(props);
    this.state = {
      userName: '',
      userNameError:'',
      password: '',
      passwordError:'',
      user: null
    }
    this.login = this.login.bind(this);
    this.handleChangeEvent = this.handleChangeEvent.bind(this);
  }

  /** onChange event  */
  handleChangeEvent(event) {
    event.preventDefault();
    const state = this.state
    state[event.target.name] = event.target.value;
    this.setState(state);
  }

  validate = () => {
    let userNameError = "";
    let passwordError = "";

    if (!this.state.userName) {
      userNameError = "please enter name";
    }


    if (!this.state.password) {
      passwordError = "please enter password";
    }

    if (nameError || passwordError) {
      this.setState({nameError, passwordError});
      return false;
    }
    return true;
  };

  handleSubmit = event => {
    event.preventDefault();
    const isValid = this.validate();
    if (isValid) {
      console.log(this.state);
      this.setState({
        userName: '',
        userNameError:'',
        password: '',
        passwordError:''
      })
    };
  }


  /** User Login successfully */
  login() {
    const isValid = this.validate();
    if (isValid) {
      console.log(this.state);
      this.setState({
        name: "",
        email: "",
        password: "",
        nameError: "",
        emailError: "",
        passwordError: "",
        sDate: "",
        endD: ""
      })
    };
    const obj = {
      userName: this.state.userName,
      password: this.state.password
    }
    API.Login(obj).
      then((findresponse) => {
        this.setState({
          user: findresponse
        })
        localStorage.setItem('token', this.state.user.data.token);
        window.location.href = '../home'
      }).catch({ status: 500, message: 'Internal Server Error' });
  }

  render() {
    return (
      <div>
        <MDBContainer>
          <MDBRow>
            <form>
              <p>Sign in</p>
              <label className="grey-text">
                Username
            					</label>
              <input
                type="text"
                className="form-control"
                name="userName"
                value={this.state.userName}
                onChange={this.handleChangeEvent}
              />
              <br />
              <label
                className="grey-text"
              >
                Your password
          						  </label>
              <input
                type="password"
                className="form-control"
                name="password"
                value={this.state.password}
                onChange={this.handleChangeEvent}
              />
              <div className="text-center mt-4">
                <MDBBtn color="indigo" onClick={this.login} disabled={!this.state.userName && !this.state.password}>Login</MDBBtn>
              </div>
              <div>
                <p><Link to="/forgotpassword">Forgot password?</Link></p>
              </div>
              <div>
                <p><Link to="/updatepassword">Update password?</Link></p>
              </div>
              <div>
                <p><Link to="/signup">signup</Link></p>
              </div>
            </form>
          </MDBRow>
        </MDBContainer>
      </div>
    );
  }
}

export default Login;
