import React from 'react';

class Login extends React.Component {

	/** First Constructor Call */
	constructor(props) {
		super(props);
	}

	render() {

		return (
			<div>
			<div className="backgroung_class">
          <div className="main_class">
            <h1 className="text-center">Login</h1>
            <Grid container spacing={1} xs={12}>
              <Grid item sm={12} xs={12}>
                <TextField
                  id="outlined-email-input"
                  label="Email"
                  type="email"
                  name="email"
                  autoComplete="email"
                  margin="normal"
                  variant="outlined"
                  value={email}
                  onChange={this.handleInputChange}
                />
              </Grid>
              <Grid item sm={12} xs={12}>
                <TextField
                  id="outlined-password-input"
                  label="Password"
                  type={this.state.hidden ? "password" : "text"}
                  name="password"
                  autoComplete="current-password"
                  margin="normal"
                  variant="outlined"
                  value={password}
                  onChange={this.handleInputChange}
                  InputProps={{
                    endAdornment: (
                      <InputAdornment position="end">
                        <IconButton
                          edge="end"
                          aria-label="Toggle password visibility"
                          onClick={this.toggleShow}
                        >
                          {password ? <VisibilityOff /> : <Visibility />}
                        </IconButton>
                      </InputAdornment>
                    ),
                  }}
                />
              </Grid>
              <Grid item sm={12} xs={12}>
                <Button color="primary" disabled={!this.state.email || !this.state.password} variant="contained" size="large" onClick={(event) => this.signInWithEmailAndPassword(event)}>
                  Login
               </Button>
              </Grid>
              <Grid item sm={12}>
                <Divider />
                <div className="text-center">
                  New User? <Button color="primary"><Link to="/sign-up">Sign Up</Link></Button>
                </div>
              </Grid>
            </Grid>
          </div>
        </div>
			</div>
		);
	}
}

export default Login;
